import React, { useEffect } from "react";

const About = ({ setProgress }) => {
  useEffect(() => {
    setProgress(30);
    setTimeout(() => setProgress(70), 500);
    setTimeout(() => setProgress(100), 1000);
  }, [setProgress]);

  return (
    <div
      style={{
        backgroundColor: "lightgray",
        color: "black",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <div className="container py-5" style={{ flex: "1" }}>
        <div
          className="card shadow-lg border-0"
          style={{ backgroundColor: "white", borderRadius: "12px" }}
        >
          <div className="card-body p-5">
            <h2 className="card-title mb-4 text-center">
              üì∞ About <span style={{ color: "red" }}>Prime News Hub</span>
            </h2>
            <p className="card-text fs-5">
              Prime News Hub is a modern news platform built with 
              <strong> React.js</strong>. Our goal is to keep you updated with 
              the latest stories in <strong>Business, Sports, Tech</strong> and more üåç.
            </p>
            <p className="card-text fs-5">
              With a <strong>clean UI</strong>, fast navigation ‚ö° and responsive 
              design üì±, it gives you a smooth reading experience on any device.
            </p>
            <div className="text-center mt-4">
              <button
                className="btn btn-danger btn-lg"
                onClick={() => (window.location.href = "/")}
              >
                üîô Back to News
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer
        style={{
          backgroundColor: "black",
          color: "white",
          textAlign: "center",
          padding: "15px",
          marginTop: "auto",
        }}
      >
        ¬© {new Date().getFullYear()} Prime News Hub. All rights reserved.
      </footer>
    </div>
  );
};

export default About;
