import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults: 0,
    };
  }

  async componentDidMount() {
    let url =
      "https://newsapi.org/v2/everything?q=india&sortBy=publishedAt&apiKey=3d82baebf7f14fed9368378d8abd32ae&page=1&pageSize=20";
    try {
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);

      this.setState({
        articles: parsedData.articles || [],      // fallback
        totalResults: parsedData.totalResults || 0,
      });
    } catch (error) {
      console.error("Error fetching news:", error);
      this.setState({ articles: [], totalResults: 0 });
    }
  }

  handlePrevClick = async () => {
    let url = `https://newsapi.org/v2/everything?q=india+politics+modi&sortBy=publishedAt&language=en&apiKey=3d82baebf7f14fed9368378d8abd32ae&page=${
      this.state.page - 1
    }&pageSize=20`;
    try {
      let data = await fetch(url);
      let parsedData = await data.json();
      this.setState({
        page: this.state.page - 1,
        articles: parsedData.articles || [],
      });
    } catch (error) {
      console.error("Error fetching previous page:", error);
    }
  };

  handleNextClick = async () => {
    if (this.state.page + 1 > Math.ceil(this.state.totalResults / 20)) {
      return;
    }
    let url = `https://newsapi.org/v2/everything?q=india+politics+modi&sortBy=publishedAt&language=en&apiKey=3d82baebf7f14fed9368378d8abd32ae&page=${
      this.state.page + 1
    }&pageSize=20`;
    try {
      let data = await fetch(url);
      let parsedData = await data.json();
      this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles || [],
      });
    } catch (error) {
      console.error("Error fetching next page:", error);
    }
  };

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center">Prime News Hub - Top Headlines</h1>
        <div className="row">
          {this.state.articles && this.state.articles.length > 0 ? (
            this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsItem
                    title={element.title ? element.title : ""}
                    description={element.description ? element.description : ""}
                    imageUrl={element.urlToImage}
                    newsUrl={element.url}
                  />
                </div>
              );
            })
          ) : (
            <p className="text-center">No articles available.</p>
          )}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.handlePrevClick}
          >
            &larr; Previous
          </button>
          <button
            disabled={this.state.page + 1 > Math.ceil(this.state.totalResults / 20)}
            type="button"
            className="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
