import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults: 0,
    };
  }

  async componentDidMount() {
    let url =
      "https://newsapi.org/v2/everything?q=india&sortBy=publishedAt&apiKey=1585ca8a71b14de9bcbd9f4a912ce52f&page=1&pageSize=20";
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
      articles: parsedData.articles ? parsedData.articles : [],
      totalResults: parsedData.totalResults ? parsedData.totalResults : 0,
    });
  }

  handlePrevClick = async () => {
    let url = `https://newsapi.org/v2/everything?q=india&sortBy=publishedAt&apiKey=1585ca8a71b14de9bcbd9f4a912ce52f&page=${
      this.state.page - 1
    }&pageSize=20`;
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
      page: this.state.page - 1,
      articles: parsedData.articles ? parsedData.articles : [],
      totalResults: parsedData.totalResults
        ? parsedData.totalResults
        : this.state.totalResults,
    });
  };

  handleNextClick = async () => {
    if (this.state.page + 1 > Math.ceil((this.state.totalResults || 0) / 20)) {
      return;
    } else {
      let url = `https://newsapi.org/v2/everything?q=india&sortBy=publishedAt&apiKey=1585ca8a71b14de9bcbd9f4a912ce52f&page=${
        this.state.page + 1
      }&pageSize=20`;
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);
      this.setState({
        page: this.state.page + 1,
        articles: parsedData.articles ? parsedData.articles : [],
        totalResults: parsedData.totalResults
          ? parsedData.totalResults
          : this.state.totalResults,
      });
    }
  };

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center">Prime News Hub - Top Headlines</h1>
        <div className="row">
          {(Array.isArray(this.state.articles)
            ? this.state.articles
            : []
          ).map((element) => (
            <div className="col-md-4" key={element.url}>
              <NewsItem
                title={element.title || ""}
                description={element.description || ""}
                imageUrl={element.urlToImage}
                newsUrl={element.url}
              />
            </div>
          ))}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.handlePrevClick}
          >
            &larr; Previous
          </button>
          <button
            disabled={
              this.state.page + 1 >
              Math.ceil((this.state.totalResults || 0) / 20)
            }
            type="button"
            className="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
