import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],   // always start with array
      loading: false,
      page: 1,
      totalResults: 0,
    };
  }

  async componentDidMount() {
    let url = `https://newsdata.io/api/1/news?apikey=pub_bdaa0077483f45bfa2eae8d7f6699a46&country=${this.props.country}&category=${this.props.category}&language=en&page=${this.state.page}`;
    this.setState({ loading: true });

    try {
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData); // check karna console me

      this.setState({
        articles: Array.isArray(parsedData.results) ? parsedData.results : [], // ðŸ‘ˆ safe array
        totalResults: parsedData.totalResults || 0,
        loading: false,
      });
    } catch (error) {
      console.error("Error fetching news:", error);
      this.setState({ articles: [], loading: false });
    }
  }

  render() {
    return (
      <div className="container my-3">
        <h2 className="text-center" style={{ margin: "35px 0px" }}>
          NewsMonkey - Top {this.props.category} Headlines
        </h2>
        <div className="row">
          {this.state.articles.length > 0 ? (
            this.state.articles.map((element, index) => {
              return (
                <div className="col-md-4" key={index}>
                  <NewsItem
                    title={element.title ? element.title : ""}
                    description={element.description ? element.description : ""}
                    imageUrl={element.image_url}
                    newsUrl={element.link}
                  />
                </div>
              );
            })
          ) : (
            <p className="text-center">No news found ðŸš«</p>
          )}
        </div>
      </div>
    );
  }
}

export default News;
