import "./App.css";

import React, { useState } from "react";
import NavBar from "./components/NavBar";
import News from "./components/News";
import About from "./components/About";
import { Switch, Route, Redirect } from "react-router-dom";
import LoadingBar from "react-top-loading-bar";

const App = () => {
  const pageSize = 6;
  const apiKey = process.env.REACT_APP_NEWS_API;
  const apiUrl = "https://cors-anywhere.herokuapp.com/https://newsapi.org/v2/top-headlines";
  const [progress, setProgress] = useState(0);

  return (
    <div>
      <NavBar />
      <LoadingBar height={3} color="#f11946" progress={progress} />

      <Switch>
        <Route exact path="/about">
          <About setProgress={setProgress} />
        </Route>

        {/* Home page */}
        <Route exact path="/">
          <News
            setProgress={setProgress}
            apiUrl={apiUrl}
            apiKey={apiKey}
            key="general"
            pageSize={pageSize}
            country="us"
            category="general"
          />
        </Route>

        {/* All other categories */}
        <Route exact path="/business">
          <News
            setProgress={setProgress}
            apiUrl={apiUrl}
            apiKey={apiKey}
            key="business"
            pageSize={pageSize}
            country="us"
            category="business"
          />
        </Route>

        <Route exact path="/entertainment">
          <News
            setProgress={setProgress}
            apiUrl={apiUrl}
            apiKey={apiKey}
            key="entertainment"
            pageSize={pageSize}
            country="us"
            category="entertainment"
          />
        </Route>

        <Route exact path="/health">
          <News
            setProgress={setProgress}
            apiUrl={apiUrl}
            apiKey={apiKey}
            key="health"
            pageSize={pageSize}
            country="us"
            category="health"
          />
        </Route>

        <Route exact path="/science">
          <News
            setProgress={setProgress}
            apiUrl={apiUrl}
            apiKey={apiKey}
            key="science"
            pageSize={pageSize}
            country="us"
            category="science"
          />
        </Route>

        <Route exact path="/sports">
          <News
            setProgress={setProgress}
            apiUrl={apiUrl}
            apiKey={apiKey}
            key="sports"
            pageSize={pageSize}
            country="us"
            category="sports"
          />
        </Route>

        <Route exact path="/technology">
          <News
            setProgress={setProgress}
            apiUrl={apiUrl}
            apiKey={apiKey}
            key="technology"
            pageSize={pageSize}
            country="us"
            category="technology"
          />
        </Route>

        <Redirect to="/" />
      </Switch>
    </div>
  );
};

export default App;
