import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from "prop-types";

export class News extends Component {
  static defaultProps = {
    country: "us",
    pageSize: 5,
    category: "general",
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    };
  }

  async componentDidMount() {
    this.fetchNews();
  }

  async fetchNews(page = 1) {
    this.setState({ loading: true });

    let url = `https://newsdata.io/api/1/news?apikey=pub_bdaa0077483f45bfa2eae8d7f6699a46&country=${this.props.country}&category=${this.props.category}&page=${page}`;

    let data = await fetch(url);
    let parsedData = await data.json();

    console.log(parsedData); // check in console

    this.setState({
      articles: parsedData.results || [],
      loading: false,
      page: page,
    });
  }

  render() {
    return (
      <div className="container my-3">
        <h2 className="text-center">Top Headlines - {this.props.category}</h2>
        {this.state.loading && <Spinner />}

        <div className="row">
          {!this.state.loading &&
            this.state.articles &&
            this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.link}>
                  <NewsItem
                    title={element.title ? element.title.slice(0, 45) : ""}
                    description={
                      element.description
                        ? element.description.slice(0, 88)
                        : ""
                    }
                    imageUrl={
                      element.image_url
                        ? element.image_url
                        : "https://via.placeholder.com/150"
                    }
                    newsUrl={element.link}
                  />
                </div>
              );
            })}
        </div>
      </div>
    );
  }
}

export default News;
