import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults: 0,
      error: null, // ✅ error message store karne ke liye
    };
  }

  fetchNews = async (page) => {
    try {
      this.setState({ loading: true, error: null });

      let url = `https://newsapi.org/v2/everything?q=india&sortBy=publishedAt&apiKey=1585ca8a71b14de9bcbd9f4a912ce52f&page=${page}&pageSize=20`;
      let data = await fetch(url);
      let parsedData = await data.json();
      console.log(parsedData);

      if (parsedData.status === "ok") {
        this.setState({
          articles: parsedData.articles || [],
          totalResults: parsedData.totalResults || 0,
          page: page,
          loading: false,
          error: null,
        });
      } else {
        // ✅ quota exceeded or other API error
        this.setState({
          articles: [],
          totalResults: 0,
          loading: false,
          error: parsedData.message || "Something went wrong!",
        });
      }
    } catch (err) {
      this.setState({
        loading: false,
        error: "Failed to fetch news. Please check your connection.",
      });
    }
  };

  async componentDidMount() {
    this.fetchNews(1);
  }

  handlePrevClick = () => {
    this.fetchNews(this.state.page - 1);
  };

  handleNextClick = () => {
    if (this.state.page + 1 <= Math.ceil(this.state.totalResults / 20)) {
      this.fetchNews(this.state.page + 1);
    }
  };

  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center">Prime News Hub - Top Headlines</h1>

        {this.state.loading && <h3 className="text-center">Loading...</h3>}
        {this.state.error && (
          <h3 className="text-center text-danger">{this.state.error}</h3>
        )}

        <div className="row">
          {!this.state.loading &&
          !this.state.error &&
          Array.isArray(this.state.articles) &&
          this.state.articles.length > 0 ? (
            this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsItem
                    title={element.title || ""}
                    description={element.description || ""}
                    imageUrl={element.urlToImage}
                    newsUrl={element.url}
                  />
                </div>
              );
            })
          ) : (
            !this.state.loading &&
            !this.state.error && (
              <h3 className="text-center">No news available</h3>
            )
          )}
        </div>

        {!this.state.error && (
          <div className="container d-flex justify-content-between my-3">
            <button
              disabled={this.state.page <= 1}
              type="button"
              className="btn btn-dark"
              onClick={this.handlePrevClick}
            >
              &larr; Previous
            </button>
            <button
              disabled={
                this.state.page + 1 > Math.ceil(this.state.totalResults / 20)
              }
              type="button"
              className="btn btn-dark"
              onClick={this.handleNextClick}
            >
              Next &rarr;
            </button>
          </div>
        )}
      </div>
    );
  }
}

export default News;
