import React, { useEffect } from "react";

const About = ({ setProgress }) => {
  useEffect(() => {
    setProgress(30);
    setTimeout(() => setProgress(70), 500);
    setTimeout(() => setProgress(100), 1000);
  }, [setProgress]);

  return (
    <div
      style={{
        backgroundColor: "lightgray",
        color: "black",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Centered Content */}
      <div
        className="d-flex justify-content-center align-items-center"
        style={{ flex: "1" }}
      >
        <div
          className="card shadow-lg border-0"
          style={{
            backgroundColor: "white",
            borderRadius: "12px",
            maxWidth: "800px",
            width: "90%",
          }}
        >
          <div className="card-body p-5">
            <h2 className="card-title mb-4 text-center">
              📰 About <span style={{ color: "red" }}>Prime News Hub</span>
            </h2>

            <p className="card-text fs-5">
              Prime News Hub is a modern, real-time news platform built using{" "}
              <strong>React.js</strong>. It is designed to keep you informed
              with the latest updates from around the world 🌍.
            </p>

            <p className="card-text fs-5">
              We cover categories like <strong>Business, Entertainment,
              Sports, Health, Technology</strong> and <strong>Science</strong>,
              so you never miss an important story. Our platform fetches
              trusted news articles using <code>NewsAPI</code> and delivers them
              in a well-structured way.
            </p>

            <p className="card-text fs-5">
              The app has a <strong>clean design</strong>, lightning-fast ⚡
              navigation, and a <strong>responsive layout</strong> 📱. Whether
              you are browsing on a laptop or a mobile, the reading experience
              will always remain smooth and user-friendly.
            </p>

            <p className="card-text fs-5">
              Prime News Hub is ideal for <span>👨‍🎓 students</span>,
              <span>✍️ writers</span>, <span>💼 professionals</span>, and
              <span>📊 researchers</span> who want quick and reliable access to
              news anytime, anywhere.
            </p>

            <div className="text-center mt-4">
              <button
                className="btn btn-danger btn-lg"
                onClick={() => (window.location.href = "/")}
              >
                🔙 Back to News
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer
        style={{
          backgroundColor: "black",
          color: "white",
          textAlign: "center",
          padding: "15px",
          marginTop: "auto",
        }}
      >
        © {new Date().getFullYear()} Prime News Hub. All rights reserved.
      </footer>
    </div>
  );
};

export default About;
