import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      nextPage: null, // API ka token
    };
  }

  async fetchNews(pageToken = "") {
    this.setState({ loading: true });

    let url = `https://newsdata.io/api/1/news?apikey=pub_bdaa0077483f45bfa2eae8d7f6699a46&country=in&language=en&category=technology`;

    if (pageToken) {
      url += `&page=${pageToken}`;
    }

    let data = await fetch(url);
    let parsedData = await data.json();

    console.log("API Response:", parsedData);

    this.setState({
      articles: parsedData.results || [],
      nextPage: parsedData.nextPage || null,
      loading: false,
    });
  }

  componentDidMount() {
    this.fetchNews();
  }

  handleNextClick = () => {
    if (this.state.nextPage) {
      this.fetchNews(this.state.nextPage);
    }
  };

  render() {
    return (
      <div className="container my-3">
        <h2 className="text-center">Top Headlines</h2>

        <div className="row">
          {this.state.articles.length > 0 ? (
            this.state.articles.map((element, index) => {
              return (
                <div className="col-md-4" key={index}>
                  <NewsItem
                    title={element.title ? element.title.slice(0, 45) : ""}
                    description={
                      element.description ? element.description.slice(0, 88) : ""
                    }
                    imageUrl={element.image_url}
                    newsUrl={element.link}
                  />
                </div>
              );
            })
          ) : (
            <h4 className="text-center">No News Found</h4>
          )}
        </div>

        <div className="container d-flex justify-content-between my-3">
          <button
            disabled={!this.state.nextPage}
            className="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next â†’
          </button>
        </div>
      </div>
    );
  }
}

export default News;
